import styled from 'styled-components';

export const ListItem = styled.li`
  list-style-type: none;
  width: 50%;
  height: 10%;
  color: #fff;
  align-content: center;
  margin: 1%;
  padding: 10px;
  border-radius: 2px;
  background-color:#3366ff;
  opacity: ${props => props.opacity };
  outline: none;
  cursor: pointer;
  border: none;

  &:hover { 
    & label {
      color: white;
    }
    background-color: #809fff;
  }

`
