import React from "react";
import {useState, useEffect } from "react";
import {Link} from "react-router-dom";
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import updateDailyTimetable from "../components/booking";

let allMonths = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

function HomePage() {
    const [value, onChange] = useState(new Date());
    const [appointments, setAppointments] = useState(null);
    const [slots, setSlots] = useState({ "3:00": "available", "4:00": "available", "5:00": "available"})

    const bookAppointment = () => {
        console.log('booked appointment for ' + value);
    }
    const getDateString = () => {
        let dateString = value.toString().split(' ').splice(0, 4).join(' ');
        return dateString;
    }

    //Import Data from MongoDB
    useEffect(() => {
        getAppointments();

        async function getAppointments() {
            const apiURL = "api/appointments"
            const response = await fetch(apiURL);
            const data = await response.json();
            const updatedData = updateDailyTimetable(9, 17, 60,
                value.getDate(), value.month, value.year, data);

            setAppointments(updatedData);
        }

    }, []);

    //
    return (
        [<div>
            <h1>Home</h1>

            <Calendar onChange={onChange}
                      value={value}/>

            <h3> Appointment Date: { getDateString() } </h3>
            <button onClick={bookAppointment}>Book Appointment</button>

            <Link to="/users">Users</Link>


            { appointments && (
                <div>
                    {appointments.map((app, index) => (
                        <h5>{ allMonths[app.month - 1] } { app.day }, { app.year }
                        at {app.hour }:{ "0" + app.minute },
                            Availability: {app.available.toString()} </h5>
                    ))}
                </div>
            ) }


        </div>]
    );
}

export default HomePage;
