import styled from 'styled-components';

export const Button = styled.button`
  border-radius: 20px;
  background-color: #0F111C;
  color: #fff;
  padding: 18px 30px;
  font-size: 20px;
  outline: none;
  cursor: pointer;
  border: none;

  &:hover { 
    & label {
      color: white;
    }
    background-color: grey;
  }

`
