import React from "react";
import {useState, useEffect } from "react";
import {Link} from "react-router-dom";
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';


function handleClickFunc(age, setAge) {
    setAge(age + 1);
}

// function handleAppointmentChange(appointmentDate, appointmentSetFunc) {
//
// }

function HomePage() {
    const [age, setAge] = useState(10);
    const handleClick = () => handleClickFunc(age, setAge);
    const [posts, setPosts] = useState(null);
    const [value, onChange] = useState(new Date());

    const getDateString = () => {
        let dateString = value.toString().split(' ').splice(0, 4).join(' ');
        return dateString;
    }

    const clickDate = () => {
        onChange(value);
        console.log("clicking on new dates" + getDateString())
    }

    //Import Data
    useEffect(() => {
        getData();

        async function getData() {
            const apiURL = "api/posts"
            const response = await fetch(apiURL);
            const data = await response.json();
            console.log(data);
            setPosts(data);
        }
    }, []);


    return (
        [<div>
            <h1>Home</h1>
            <Calendar onChange={onChange}
                      value={value}/>
            <h3> Appointment Date: { getDateString() } </h3>
            { posts && (
                <div>
                    {posts.map ((post, index) => (
                        <h5>{post.description}</h5>
                    ))}
                </div>
            ) }
            <h3>\I am { age } years old</h3>
            <button onClick = {handleClick}> Click </button>
            <Link to="/users">Users</Link>
        </div>]
    );
}

export default HomePage;
