import React from "react";
import {useState, useEffect } from "react";
import {Link} from "react-router-dom";
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import calendar from "../components/calender";

let allMonths = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

function HomePage() {
    const [posts, setPosts] = useState(null);
    const [value, onChange] = useState(new Date());
    const [appointments, setAppointments] = useState(null);

    const bookAppointment = () => {
        console.log('booked appointment for ' + value);
    }
    const getDateString = () => {
        let dateString = value.toString().split(' ').splice(0, 4).join(' ');
        return dateString;
    }

    //Import Data from MongoDB
    useEffect(() => {
        getData();

        async function getData() {
            const apiURL = "api/posts"
            const response = await fetch(apiURL);
            const data = await response.json();
            console.log(data);
            setPosts(data);
        }
    }, []);
    useEffect(() => {
        getAppointments();

        async function getAppointments() {
            const apiURL = "api/appointments"
            const response = await fetch(apiURL);
            const data = await response.json();
            setAppointments(data);
        }
    }, []);


    return (
        [<div>
            <h1>Home</h1>
            <Calendar onChange={onChange}
                      value={value}/>
            <h3> Appointment Date: { getDateString() } </h3>
            <button onClick={bookAppointment}>Book Appointment</button>
            { posts && (
                <div>
                    {posts.map((post, index) => (
                        <h5>{post.description}</h5>
                    ))}
                </div>
            ) }
            { appointments && (
                <div>
                    {appointments.map((app, index) => (
                        <h5>Booked: {allMonths[app.month - 1]}, { app.year } at {app.hour }:{ app.minute }</h5>
                    ))}
                </div>
            ) }

            <Link to="/users">Users</Link>
        </div>]
    );
}

export default HomePage;
