import styled from 'styled-components';

export const ListItem = styled.li`
  width: 15%;
  height: 10%;
  color: #fff;
  margin: 1%;
  padding: 4px;
  background-color:#3366ff;
  outline: none;
  cursor: pointer;
  border: none;

  &:hover { 
    & label {
      color: white;
    }
    background-color: grey;
  }

`
